import { Navbar, Buttons, Button } from "react-bulma-companion";
import { Link, useNavigate } from "react-router-dom";
import { useState } from "react";
const NavbarComp = () => {
  const [moviesTab, setMoviesTab] = useState(true);
  const [subscriptionsTab, setSubscriptionTab] = useState(false);
  const [usersmTab, setUsersmTab] = useState(false);

  const navigate = useNavigate();

  const onClickMoviesTabHandler = () => {
    setMoviesTab(true);
    setSubscriptionTab(false);
    setUsersmTab(false);
    navigate("/movies");
  };

  const onClickSubscriptionsTabHandler = () => {
    setSubscriptionTab(true);
    setMoviesTab(false);
    setUsersmTab(false);
    navigate("/subscriptions");
  };

  const onClickUsermTabHandler = () => {
    setUsersmTab(true);
    setMoviesTab(false);
    setSubscriptionTab(false);
    navigate("/usersmanagement");
  };
  return (
    <Navbar>
      <Navbar.Menu>
        <Navbar.Start>
          <Navbar.Item
            tab
            onClick={onClickMoviesTabHandler}
            active={moviesTab ? true : false}
          >
            Movies
          </Navbar.Item>
          <Navbar.Item
            tab
            onClick={onClickSubscriptionsTabHandler}
            active={subscriptionsTab ? true : false}
          >
            Subscriptions
          </Navbar.Item>
          <Navbar.Item
            tab
            onClick={onClickUsermTabHandler}
            active={usersmTab ? true : false}
          >
            Users Management
          </Navbar.Item>
          <Navbar.Item tab>Logout</Navbar.Item>
        </Navbar.Start>
        <Navbar.End>
          <Navbar.Item>
            <Buttons>
              <Button color="primary" component={Link} to="/createaccount">
                <strong>Create Account</strong>
              </Button>
              <Button color="light" component={Link} to="/">
                Login
              </Button>
            </Buttons>
          </Navbar.Item>
        </Navbar.End>
      </Navbar.Menu>
    </Navbar>
  );
};

export default NavbarComp;
