import { Box, Button, Title, Buttons } from "react-bulma-companion";
import InputMembers from "./InputMembers";
import { selectMember, getMember } from "../store/members";
import { useSelector, useDispatch } from "react-redux";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const EditMember = () => {
  const memberId = useSelector((state) => state.memberId.id);
  const dispatch = useDispatch();
  const navigate = useNavigate();

  useEffect(() => {
    dispatch(getMember(memberId));
  }, [dispatch, memberId]);

  const member = useSelector(selectMember);
  const [inputValue, setInputValue] = useState({
    name: "",
    email: "",
    city: "",
  });

  const onChangeInput = (e) => {
    const { name, value } = e.target;
    setInputValue((prevstate) => ({
      ...prevstate,
      [name]: value,
    }));
  };

  const onCancelClick = () => {
    navigate("/allmembers");
  };

  console.log(member);
  return (
    <Box
      style={{
        maxWidth: "25rem",
        marginTop: "2rem",
        marginLeft: "auto",
        marginRight: "auto",
      }}
    >
      <Title>Edit Member</Title>
      <InputMembers
        plchName={member.name}
        plchEmail={member.email}
        plchCity={member.city}
        nameValue={inputValue.name}
        emailValue={inputValue.email}
        cityValue={inputValue.city}
        onChangeName={onChangeInput}
        onChangeEmail={onChangeInput}
        onChangeCity={onChangeInput}
      />
      <Buttons className="is-flex is-justify-content-center">
        <Button>Update</Button>
        <Button onClick={onCancelClick}>Cancel</Button>
      </Buttons>
    </Box>
  );
};

export default EditMember;
