import { Field, Input, Label, Control } from "react-bulma-companion";
import { useState } from "react";

const InputMovies = (props) => {
  const [InputValues, setInputValues] = useState({
    name: "",
    genres: "",
    image: "",
    premiered: "",
  });

  const onChangeInput = (e) => {
    const { name, value } = e.target;
    setInputValues((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  return (
    <div>
      <Field>
        <Label size="small">Name</Label>
        <Control className="has-icons-left">
          <Input
            name="name"
            type="text"
            size="small"
            defaultValue={props.defNameValue}
            value={props.nameValue}
            onChange={onChangeInput}
          />
        </Control>
      </Field>
      <Field>
        <Label size="small">Genres</Label>
        <Control className="has-icons-left">
          <Input
            name="genres"
            type="text"
            size="small"
            defaultValue={props.defGenresValue}
            value={props.genresValue}
            onChange={onChangeInput}
          />
        </Control>
      </Field>
      <Field>
        <Label size="small">Image Url</Label>
        <Control className="has-icons-left">
          <Input
            name="imgurl"
            type="text"
            size="small"
            defaultValue={props.defImgurlValue}
            value={props.imgurlValue}
            onChange={onChangeInput}
          />
        </Control>
      </Field>
      <Field>
        <Label size="small">Premired</Label>
        <Control className="has-icons-left">
          <Input
            name="premired"
            type="text"
            size="small"
            defaultValue={props.defPremieredValue}
            value={props.premieredValue}
            onChange={onChangeInput}
          />
        </Control>
      </Field>
    </div>
  );
};

export default InputMovies;
