            active={allMoviesTab ? true : false}
          >
            <Tabs.Link>All Movies</Tabs.Link>
          </Tabs.ListItem>
          <Tabs.ListItem
            onClick={addMovieTabHandler}
            active={addMovieTab ? true : false}
          >
            <Tabs.Link>Add Movie</Tabs.Link>
          </Tabs.ListItem>
        </Tabs.List>
      </Tabs>

      {allMoviesTab ? <AllMovies /> : <AddMovie />} */}
    </div>
  );
};

export default Movies;
