import { useState, useEffect } from "react";
import { Box, Button, Title, Buttons } from "react-bulma-companion";
import InputMovies from "./InputMovies";
import { useSelector, useDispatch } from "react-redux";
import { getMovie, selectOneMovie } from "../store/movies";
import { useNavigate } from "react-router-dom";

const EditMovie = (props) => {
  const movieId = useSelector((state) => state.movieId.id);
  const dispatch = useDispatch();
  const navigate = useNavigate();

  useEffect(() => {
    dispatch(getMovie(movieId));
  }, [dispatch, movieId]);
  const movie = useSelector(selectOneMovie);

  const [inputValue, setInputValue] = useState({
    name: "",
    genres: "",
    image: "",
    premiered: "",
  });

  const onChangeInput = (e) => {
    const { name, value } = e.target;
    setInputValue((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const onCancelClick = () => {
    navigate("/allmovies");
  };

  return (
    <Box
      style={{
        maxWidth: "25rem",
        marginTop: "2rem",
        marginLeft: "auto",
        marginRight: "auto",
      }}
    >
      <Title>Edit Movie</Title>
      <InputMovies
        nameValue={inputValue.name}
        plchName={movie.name}
        plchGenres={
          Object.keys(movie).length !== 0
            ? movie.genres.map((genre) => {
                return genre;
              })
            : null
        }
        plchImg={Object.keys(movie).length !== 0 ? movie.image.medium : null}
        plchpremiered={movie.premiered}
        onChangeName={onChangeInput}
        genresValue={inputValue.genres}
        onChangeGenres={onChangeInput}
        imgurlValue={inputValue.image}
        onChangeImg={onChangeInput}
        premieredValue={inputValue.premiered}
        onChangePremiered={onChangeInput}
      />
      <Buttons className="is-flex is-justify-content-center">
        <Button>Update</Button>
        <Button onClick={onCancelClick}>Cancel</Button>
      </Buttons>
    </Box>
  );
};

export default EditMovie;
