import { useState, useEffect } from "react";
import { Card, Title, Buttons, Button } from "react-bulma-companion";
import EditMovie from "./EditMovie";
import { getAllMovies, selectAllMovies } from "../store/movies";
import { useSelector, useDispatch } from "react-redux";

const AllMovies = () => {
  const dispatch = useDispatch();
  const movies = useSelector(selectAllMovies);

  useEffect(() => {
    dispatch(getAllMovies());
  }, [dispatch]);

  const [movieId, setMovieId] = useState("");

  const onEditClickHandler = (id) => {
    setMovieId(id);
  };

  return (
    <div>
      <Title>All Movies</Title>
      {movies.map((movie) => {
        return (
          <Card
            style={{
              maxWidth: "25rem",
              marginBottom: "2rem",
              marginLeft: "auto",
              marginRight: "auto",
            }}
            key={movie._id}
          >
            <Title size="5">{movie.name}</Title>
            <h3>Genres: {movie.genres}</h3>
            <img alt="movie" src={movie.image.medium} />
            <Buttons>
              <Button onClick={() => onEditClickHandler(movie._id)}>
                Edit
              </Button>
              <Button>Delete</Button>
            </Buttons>
          </Card>
        );
      })}
      {true ? null : <EditMovie movieId={movieId} />}
    </div>
  );
};

export default AllMovies;
