import { useState, useEffect } from "react";
import { Box, Button, Title, Buttons } from "react-bulma-companion";
import InputMovies from "./InputMovies";
import { useSelector, useDispatch } from "react-redux";
import { getMovie, selectOneMovie } from "../store/movies";

const EditMovie = () => {
  const movieId = useSelector((state) => state.movieId.id);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getMovie(movieId));
  }, [dispatch]);
  const movie = useSelector(selectOneMovie);
  console.log(movie);
  return (
    <Box
      style={{
        maxWidth: "25rem",
        marginTop: "2rem",
        marginLeft: "auto",
        marginRight: "auto",
      }}
    >
      <Title>Edit Movie</Title>
      <InputMovies
        nameValue={movie ? movie.name : null}
        imgurlValue={
          Object.keys(movie).length !== 0 ? movie.image.medium : null
        }
        premieredValue={movie ? movie.premiered : null}
        genresValue={
          Object.keys(movie).length !== 0
            ? movie.genres.map((genre) => {
                return genre;
              })
            : null
        }
      />
      <Buttons className="is-flex is-justify-content-center">
        <Button>Update</Button>
        <Button>Cancel</Button>
      </Buttons>
    </Box>
  );
};

export default EditMovie;
