import { Navbar } from "react-bulma-companion";
import { useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import { reset } from "../store/auth";
import { useDispatch, useSelector } from "react-redux";
import { getMainPage } from "../store/auth";

const NavbarComp = () => {
  const [moviesTab, setMoviesTab] = useState(false);
  const [subscriptionsTab, setSubscriptionTab] = useState(false);
  const [usersmTab, setUsersmTab] = useState(false);

  const navigate = useNavigate();
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getMainPage());
  }, [dispatch]);
  const permissions = useSelector((state) => state.auth.permissions);

  const onClickMoviesTabHandler = () => {
    setMoviesTab(true);
    setSubscriptionTab(false);
    setUsersmTab(false);
    navigate("/movies");
  };

  const onClickSubscriptionsTabHandler = () => {
    setSubscriptionTab(true);
    setMoviesTab(false);
    setUsersmTab(false);
    navigate("/subscriptions");
  };

  const onClickUsermTabHandler = () => {
    setUsersmTab(true);
    setMoviesTab(false);
    setSubscriptionTab(false);
    navigate("/usersmanagement");
  };

  const onClickLogout = () => {
    sessionStorage.removeItem("token");
    dispatch(reset());
    navigate("/");
  };

  const adminCheck = () => {
    if (Object.keys(permissions).length !== 0) {
      for (const property in permissions) {
        if (property === "Admin" && permissions[property] === false) {
          return false;
        } else if (property === "Admin" && permissions[property] === true) {
          return true;
        }
      }
    }
  };
  return (
    <Navbar>
      <Navbar.Menu>
        <Navbar.Start>
          <Navbar.Item
            tab
            onClick={onClickMoviesTabHandler}
            active={moviesTab ? true : false}
          >
            Movies
          </Navbar.Item>
          <Navbar.Item
            tab
            onClick={onClickSubscriptionsTabHandler}
            active={subscriptionsTab ? true : false}
          >
            Subscriptions
          </Navbar.Item>
          {adminCheck() === true ? (
            <Navbar.Item
              tab
              onClick={onClickUsermTabHandler}
              active={usersmTab ? true : false}
            >
              Users Management
            </Navbar.Item>
          ) : null}

          <Navbar.Item tab onClick={onClickLogout}>
            Logout
          </Navbar.Item>
        </Navbar.Start>
      </Navbar.Menu>
    </Navbar>
  );
};

export default NavbarComp;
