import { useState, useEffect } from "react";
import { Box, Button, Title, Buttons } from "react-bulma-companion";
import InputMovies from "./InputMovies";
import { useSelector, useDispatch } from "react-redux";
import { getMovie, selectAllMovies } from "../store/movies";

const EditMovie = () => {
  const movieId = useSelector((state) => state.movieId.id);
  const dispatch = useDispatch();
  const movie = useSelector(selectAllMovies);
  const imgUrl = movie.image.medium;

  useEffect(() => {
    dispatch(getMovie(movieId));
  }, [dispatch]);

  return (
    <Box
      style={{
        maxWidth: "25rem",
        marginTop: "2rem",
        marginLeft: "auto",
        marginRight: "auto",
      }}
    >
      <Title>Edit Movie</Title>
      <InputMovies
        nameValue={movie ? movie.name : null}
        imgurlValue={imgUrl}
        premieredValue={movie ? movie.premiered : null}
        // genresValue={
        //   movie
        //     ? movie.genres.map((genre) => {
        //         return genre;
        //       })
        //     : null
        // }
      />
      <Buttons className="is-flex is-justify-content-center">
        <Button>Update</Button>
        <Button>Cancel</Button>
      </Buttons>
    </Box>
  );
};

export default EditMovie;
